def merge_the_tools(string, k):
    iter =len(string)//k
    for i in range(iter):
        word = string[(i*k):(i+1)*k]
        wordlist = []
        for j in word:
            if j not in wordlist:
                wordlist.append(j)
        print("".join(wordlist))
